export class NavMenuComponent {
  constructor(customerService, panierService, favoritesService, productService, auth) {
    this.customerService = customerService;
    this.panierService = panierService;
    this.favoritesService = favoritesService;
    this.productService = productService;
    this.auth = auth;
    this.customer = {
      id: 0,
      email: '',
      password: ''
    };
    this.panierInfo = {
      products: [],
      totalPrice: 0,
      nbProducts: 0
    };
    this.favorites = {
      favorites: [],
      nbFavorites: 0
    };
    this.products = [];
    this.pruductFavoris = [];
    this.isExpanded = false;
    this.isSidebar = false;
    this.isSidebarChange = false;
    this.isCustomerImage = false;
    this.isDrop = false;
    this.isSticky = false; //--------------------------------
    //Sidebar Mon Panier
    //$event is for disable <a href=""

    this.isSidebarMonpanier = false; //--------------------------------
    //Sidebar with login

    this.isSidebarLogin = false; //--------------------------------
    //Sidebar with favorites

    this.isSidebarFavorites = false;
    console.log("auth", auth.user$);
  }

  ngOnInit() {
    this.productService.getProducts().subscribe(res => {
      if (res) this.products = res;
      res.forEach((element, index, array) => {
        this.favorites.favorites.forEach((element_f, index_f, array_f) => {
          if (element.id == element_f) this.pruductFavoris.push(element);
        });
      });
    });
    this.favorites = this.favoritesService.getFavoritesLocalStorage();
    this.favoritesService.$favorites.subscribe(res => {
      this.favorites = res;
      this.pruductFavoris = [];
      this.products.forEach((element, index, array) => {
        this.favorites.favorites.forEach((element_f, index_f, array_f) => {
          if (element.id == element_f) this.pruductFavoris.push(element);
        });
      });
    });
    this.panierService.$panier.subscribe(res => {
      this.panierInfo = res;
    });
    this.panierInfo = this.panierService.getProductsLocalStorage();
  }

  onWindowScroll() {
    if (window.scrollY > 90) {
      this.isSticky = true;
    } else {
      this.isSticky = false;
    }
  }

  isEmptyObject(obj) {
    return obj && Object.keys(obj).length === 0;
  }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    if (this.isSidebar) {
      this.isSidebar = !this.isSidebar;
      this.isSidebarFavorites = false;
      this.isSidebarLogin = false;
      this.isSidebarMonpanier = false;
    }

    this.isExpanded = !this.isExpanded;
  } //Sleep function for all Sidebars


  sleep(time) {
    return new Promise(resolve => setTimeout(resolve, time));
  } //--------------------------------
  //Sidebar open/close with icon 'flash'


  onSidebar($event) {
    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebar = !this.isSidebar;
    if (this.isSidebarFavorites) this.isSidebarFavorites = false;
    if (this.isSidebarLogin) this.isSidebarLogin = false;
    if (this.isSidebarMonpanier) this.isSidebarMonpanier = false;
  }

  onSidebarMonpanier($event) {
    $event.preventDefault();

    if (this.isSidebarFavorites || this.isSidebarLogin) {
      this.isSidebarLogin = false;
      this.isSidebarFavorites = false;
      this.isSidebar = !this.isSidebar;
      this.sleep(900).then(() => {
        this.isSidebar = !this.isSidebar;
        this.isSidebarMonpanier = !this.isSidebarMonpanier;
      });
      return;
    }

    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebarMonpanier = !this.isSidebarMonpanier;
    this.isSidebar = !this.isSidebar;
  }

  onSidebarLogin($event) {
    $event.preventDefault();

    if (this.isSidebarFavorites || this.isSidebarMonpanier) {
      this.isSidebarFavorites = false;
      this.isSidebarMonpanier = false;
      this.isSidebar = !this.isSidebar;
      this.sleep(900).then(() => {
        this.isSidebar = !this.isSidebar;
        this.isSidebarLogin = !this.isSidebarLogin;
      });
      return;
    }

    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebarLogin = !this.isSidebarLogin;
    this.isSidebar = !this.isSidebar;
  }

  onSidebarFavorites($event) {
    $event.preventDefault();

    if (this.isSidebarLogin || this.isSidebarMonpanier) {
      this.isSidebarLogin = false;
      this.isSidebarMonpanier = false;
      this.isSidebar = !this.isSidebar;
      this.sleep(550).then(() => {
        this.isSidebar = !this.isSidebar;
        this.isSidebarFavorites = !this.isSidebarFavorites;
      });
      return;
    }

    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebarFavorites = !this.isSidebarFavorites;
    this.isSidebar = !this.isSidebar;
  } //--------------------------------
  //btn in Slidebar


  onDeleteFromFavorites(id) {
    this.favoritesService.deleteFavoritesLocalStorage(id);
  }

  deleteFromPanier(id, price) {
    this.panierService.deleteProductLocalStorage(id, price);
  }

  addToPanier(product) {
    this.panierService.addProductLocalStorage(product, 1);
  }

  addAllToPanier() {
    for (let i = 0; i < this.pruductFavoris.length; i++) {
      this.panierService.addProductLocalStorage(this.pruductFavoris[i], 1);
    }
  }

  onSidebarSideChange() {
    this.isSidebarChange = !this.isSidebarChange;
  }

  onDrop() {
    this.isDrop = !this.isDrop;
  }

  mouse() {
    if (this.isDrop) this.isDrop = false;
  }

  submit(f) {
    this.customerService.create(this.customer).subscribe(res => {
      console.log("res: ", res);
      this.isCustomerImage = true;

      (function repeat() {
        setTimeout(function () {
          this.isCustomerImage = false;
        }, 2000);
      })();
    }, err => {});
  }

}

