_ngcontent-%COMP%]   ul[_ngcontent-%COMP%]   li[_ngcontent-%COMP%] {\n  width: 100%;\n  margin: auto;\n  font-size: 1.5rem;\n  margin: 1.5rem;\n  padding-right:3rem;\n  list-style: none;\n  display:flex;\n  justify-content:space-between;\n}\n\n\n@media (max-width: 1050px) {\n\n  .sidebar-login[_ngcontent-%COMP%] {\n    height: calc(100vh - 144px);\n  }\n}\n@media (max-width: 500px) {\n  .sidebar-login[_ngcontent-%COMP%] {\n    width: 100%;\n    padding: 10px;\n  }\n\n  .hide[_ngcontent-%COMP%] {\n    margin-right: -500px;\n  }\n\n  .sidebar-login[_ngcontent-%COMP%]   .sidebar-label[_ngcontent-%COMP%] {\n    left: 150px;\n  }\n\n  .sidebar-login[_ngcontent-%COMP%]   .sidebar-arrow[_ngcontent-%COMP%] {\n    font-size: 4rem !important;\n  }\n\n  .sidebar-login[_ngcontent-%COMP%]   .sidebar-login-title[_ngcontent-%COMP%] {\n    font-size: 3rem;\n  \n  }\n\n  .sidebar-login[_ngcontent-%COMP%]   .sidebar-creercompte[_ngcontent-%COMP%] {\n    font-size: 2.1rem;\n   \n  }\n\n  .sidebar-login[_ngcontent-%COMP%]   .btn[_ngcontent-%COMP%]{\n      font-size:3rem;\n      width:90%;\n      height:5rem;\n  }\n\n\n\n}\n/*# sourceMappingURL=data:application/json;base64, */"]
