import { Component, OnInit, HostListener } from '@angular/core';
import { CustomerService } from '../../services/customer.service';
import { SaveCustomer } from '../../models/customer';
import { PanierService } from '../../services/panier.service';
import { FavoritesService } from '../../services/favorites.service';
import { ProductService } from '../../services/product.service';
import { Favorites } from '../../models/favorites';

import { PanierInfo } from '../../models/panierInfo';
import { Product } from '../../models/product';


import { AuthService } from '@auth0/auth0-angular';


@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {

  customer: SaveCustomer = {
    id: 0,
    email: '',
    password: ''

  }

  panierInfo: PanierInfo = {
    products: [],
    totalPrice: 0,
    nbProducts: 0
  }

  favorites: Favorites = {
    favorites: [],
    nbFavorites: 0
  }

  products: Product[] = [];

  pruductFavoris: Product[] = [];


  isExpanded = false;
  isSidebar = false;
  isSidebarChange = false;
  isCustomerImage = false;
  isDrop = false;




  constructor(
    private customerService: CustomerService,
    private panierService: PanierService,
    private favoritesService: FavoritesService,
    private productService: ProductService,
    public auth: AuthService
  ) {
    console.log("auth", auth.user$);


  }




  ngOnInit() {

    this.productService.getProducts().subscribe((res: Product[]) => {
      if (res)
        this.products = res;

      res.forEach((element, index, array) => {
        this.favorites.favorites.forEach((element_f, index_f, array_f) => {
          if (element.id == element_f)
            this.pruductFavoris.push(element);
        })
      })

    })


    this.favorites = this.favoritesService.getFavoritesLocalStorage();


    this.favoritesService.$favorites.subscribe((res: Favorites) => {
      this.favorites = res;

      this.pruductFavoris = [];

      this.products.forEach((element, index, array) => {
        this.favorites.favorites.forEach((element_f, index_f, array_f) => {
          if (element.id == element_f)
            this.pruductFavoris.push(element);
        })
      })



    })


    this.panierService.$panier.subscribe((res: PanierInfo) => {
      this.panierInfo = res;
    })

    this.panierInfo = this.panierService.getProductsLocalStorage();


  }





  isSticky = false;

  @HostListener('window:scroll', [])
  onWindowScroll() {
    if (window.scrollY > 90) {
      this.isSticky = true;
    } else {
      this.isSticky = false;
    }
  }

  isEmptyObject(obj:any) {
    return (obj && (Object.keys(obj).length === 0));
  }



  collapse() {
    this.isExpanded = false;
  }


  toggle() {
    if (this.isSidebar) {
      this.isSidebar = !this.isSidebar;
      this.isSidebarFavorites = false;
      this.isSidebarLogin = false;
      this.isSidebarMonpanier = false;
    }


    this.isExpanded = !this.isExpanded;
  }


  //Sleep function for all Sidebars

  sleep(time: number) {
    return new Promise((resolve) => setTimeout(resolve, time));
  }

  //--------------------------------






  //Sidebar open/close with icon 'flash'

  onSidebar($event:any) {
    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }
    this.isSidebar = !this.isSidebar;

    if (this.isSidebarFavorites)
      this.isSidebarFavorites = false;

    if (this.isSidebarLogin)
      this.isSidebarLogin = false;

    if (this.isSidebarMonpanier)
      this.isSidebarMonpanier = false;

  }

  //--------------------------------




  //Sidebar Mon Panier
  //$event is for disable <a href=""

  isSidebarMonpanier = false;

  onSidebarMonpanier($event:any) {
    $event.preventDefault()

    if (this.isSidebarFavorites || this.isSidebarLogin) {
      this.isSidebarLogin = false;
      this.isSidebarFavorites = false;
      this.isSidebar = !this.isSidebar;

      this.sleep(900).then(() => {
        this.isSidebar = !this.isSidebar;
        this.isSidebarMonpanier = !this.isSidebarMonpanier;
      });

      return;
    }


    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebarMonpanier = !this.isSidebarMonpanier;

    this.isSidebar = !this.isSidebar;

  }

  //--------------------------------




  //Sidebar with login

  isSidebarLogin = false;

  onSidebarLogin($event:any) {
    $event.preventDefault()

    if (this.isSidebarFavorites || this.isSidebarMonpanier) {
      this.isSidebarFavorites = false;
      this.isSidebarMonpanier = false;

      this.isSidebar = !this.isSidebar;

      this.sleep(900).then(() => {
        this.isSidebar = !this.isSidebar;
        this.isSidebarLogin = !this.isSidebarLogin;
      });

      return;
    }



    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebarLogin = !this.isSidebarLogin;

    this.isSidebar = !this.isSidebar;
  }

  //--------------------------------






  //Sidebar with favorites

  isSidebarFavorites = false;

  onSidebarFavorites($event:any) {
    $event.preventDefault();

    if (this.isSidebarLogin || this.isSidebarMonpanier) {
      this.isSidebarLogin = false;
      this.isSidebarMonpanier = false;

      this.isSidebar = !this.isSidebar;

      this.sleep(550).then(() => {
        this.isSidebar = !this.isSidebar;
        this.isSidebarFavorites = !this.isSidebarFavorites;
      });

      return;
    }



    if (this.isExpanded) {
      this.isExpanded = !this.isExpanded;
    }

    this.isSidebarFavorites = !this.isSidebarFavorites;


    this.isSidebar = !this.isSidebar;
  }

  //--------------------------------







  //btn in Slidebar

  onDeleteFromFavorites(id: number) {
    this.favoritesService.deleteFavoritesLocalStorage(id);
  }
  deleteFromPanier(id: number, price: number) {
    this.panierService.deleteProductLocalStorage(id, price);
  }

  addToPanier(product: Product) {
    this.panierService.addProductLocalStorage(product, 1)
  }

  addAllToPanier() {
    for (let i = 0; i < this.pruductFavoris.length; i++) {
      this.panierService.addProductLocalStorage(this.pruductFavoris[i], 1)
    }

  }



  onSidebarSideChange() {
    this.isSidebarChange = !this.isSidebarChange;
  }


  onDrop() {
    this.isDrop = !this.isDrop;
  }




  mouse() {
    if (this.isDrop)
      this.isDrop = false;
  }




  submit(f:any) {
    this.customerService.create(this.customer)
      .subscribe(
        res => {
          console.log("res: ", res)
          this.isCustomerImage = true;


          (function repeat() {
            setTimeout(function (this:any) {
              this.isCustomerImage = false;
            }, 2000);
          })();



        },
        err => {
        }
      )
  }


}
<header>


    <div class="header-1">
        <!-- <a href="#" class="logo"><i class="fas fa-shopping-basket"></i>groco</a> -->
        <div id="logo">
            <img id="logo_img" src="../../../assets/images/point.png" alt="point gps" />
        </div>
        <span class="navcityname">Blois</span>

        <span class="navdelivery">
            Livraison Courses À Domicile
            <i class="navcityname fas fa-shipping-fast"></i>
        </span>

        <form action="" class="search-box-container">
            <input type="search"
                   id="search-box"
                   placeholder="Rechercher un produit" />
            <label for="search-box" class="fas fa-search"></label>
        </form>
    </div>




    <div class="header-2" [class.sticky-header-2]="isSticky"  >
        <div id="menu-bar" class="fas fa-bars" (click)="toggle()"></div>

        <nav class="navbar" [class.active]="isExpanded">
            <a class="" href="#home">Accueil</a>
            <a class="" href="#categories">Catégories</a>
            <a class="" href="#category">Promotion</a>
            <a class="" href="#contact">Contact</a>

            <a class="small-drop " (click)="onDrop()">
                Administrateur
                <i class="fas fa-caret-down"></i>

                <ul class="drop-list" [class.show-drop]="isDrop">
                    <li><a routerLink="/admin/product/">Gestion des produits</a></li>
                    <li><a routerLink="/admin/order/" href="#">Gestion des orders</a></li>
                </ul>

            </a>



        </nav>

        <!--NavBars Icons-->

        <div class="icons icons-jb">

            <div class="icon-box">
                <a href="#" class="fas fa-shopping-cart" (click)="onSidebarMonpanier($event)">
                    <div *ngIf="panierInfo.nbProducts == 0">Mon panier</div>
                    <div *ngIf="panierInfo.nbProducts > 0">{{panierInfo.totalPrice | currency: "EUR"}} </div><div class="nb-items nb-panier"*ngIf="panierInfo.nbProducts > 0">{{panierInfo.nbProducts}}</div>
                </a>
            </div>

            <div class="icon-box">
                <a href="#" class="fas fa-heart" (click)="onSidebarFavorites($event)">
                    <div>Favoris</div><div class="nb-items" *ngIf="favorites.nbFavorites > 0">{{favorites.nbFavorites}}</div>
                </a>
            </div>

            <div class="icon-box">
                <a href="#" class="fas fa-user-circle" (click)="onSidebarLogin($event)">
                    <!--<div *ngIf="auth.user$ | async as user">S'identifier</div>-->
                    <div class="user" *ngIf="auth.user$ | async as user; else templateName"> {{user.name | titlecase}}</div>

                    <ng-template #templateName>
                       <div> S'identifier </div>
                    </ng-template>


                </a>
            </div>
        </div>


    </div>





    <!--Sidebar Login-->


    <div class="sidebar-login " [class.hide]="!isSidebar" [class.sticky-sidebar]="isSticky">

        <div *ngIf="isSidebarLogin">
            <i class="far fa-arrow-alt-circle-right sidebar-arrow" (click)="onSidebar($event)"></i>

            <p *ngIf="!isSidebarChange" class="sidebar-login-title">Accéder à votre compte</p>
            <!--<p *ngIf="isSidebarChange" class="sidebar-login-title">Créer Un Compte</p>-->

            <!--<p *ngIf="!isSidebarChange" class="sidebar-creercompte" (click)="onSidebarSideChange()">Créer un compte</p>
            <p *ngIf="isSidebarChange" class="sidebar-creercompte" (click)="onSidebarSideChange()">Accéder à votre compte</p>-->



            <form *ngIf="!isSidebarChange" #f="ngForm" (ngSubmit)="submit(f)" class="form-login">


                <app-auth-button></app-auth-button>

                <img *ngIf="isCustomerImage" src="../../../assets/images/customer_ok.png" alt="customer_ok" />


            </form>


            <form *ngIf="isSidebarChange" class="form-register">

                <label class="sidebar-label" for="email">Email</label>
                <input ngModel name="email" class="sidebar-input" type="text" id="email" />
                <br>
                <label class="sidebar-label" for="mdp">Mot de passe</label>
                <input ngModel name="password" class="sidebar-input" type="text" id="password" />
                <br>
                <label class="sidebar-label" for="mdp">Confirm mot de passe</label>
                <input ngModel name="password_2" class="sidebar-input" type="text" id="password_2" />

                <button class="btn btn-sidebar-login">Créer Mon Compte</button>

            </form>

        </div>


        <!--Sidebar Mes Favoris -->

        <div *ngIf="isSidebarFavorites" style="text-align:center;">
            <i class="far fa-arrow-alt-circle-right sidebar-arrow" (click)="onSidebar($event)"></i>

            <p class="sidebar-login-title">Mes Produits Préférés </p>
            <p class="sidebar-creercompte">La liste</p>

            <ul>
                <li class="panier-product" *ngFor="let p of pruductFavoris;let indexOfelement=index;">
                    <span>{{p.title}}</span>
                    <span>
                        {{p.price | currency: "EUR"}}
                        <i (click)="addToPanier(p)" class="fas fa-cart-arrow-down" style="color: rgb(22,199,19);margin:0.5rem;"></i>
                        <i (click)="onDeleteFromFavorites(p.id)" class="fas fa-minus-circle" style="color: rgb(254,38,37);"></i>
                    </span>

                </li>
            </ul>

            <button (click)="addAllToPanier()" class="btn">Ajouter tout au panier</button>


        </div>


        <!-- Sidebar Mon Panier -->

        <div *ngIf="isSidebarMonpanier" style="text-align:center;">
            <i class="far fa-arrow-alt-circle-right sidebar-arrow" (click)="onSidebar($event)"></i>

            <p class="sidebar-login-title">Mon Panier <i class="fas fa-shopping-cart" style="color: rgb(2,113,220);"></i> </p>
            <p class="sidebar-creercompte">La liste ({{panierInfo.nbProducts}})</p>

            <ul>
                <li class="panier-product" *ngFor="let p of panierInfo.products;let indexOfelement=index;">
                    <span>{{p.title}}</span>
                    <span>
                        {{p.price | currency: "EUR"}}
                        <i (click)="deleteFromPanier(p.id,p.price)" class="fas fa-minus-circle" style="color: rgb(254,38,37);"></i>
                    </span>

                </li>
            </ul>

            <button (click)="addAllToPanier()" class="btn">Payer <i class="far fa-credit-card"></i> <span style="margin-left:0.5rem;">{{panierInfo.totalPrice | currency: "EUR"}}</span> </button>


        </div>


    </div>





</header>

<div *ngIf="isSticky" style="height:90px; display:inline-block;">anti-margin calapse </div>
