import { Component, Inject } from '@angular/core';
import { AuthService } from '@auth0/auth0-angular';
import { UserAuthService } from '../../services/user-auth.service';

import { DOCUMENT } from '@angular/common';


@Component({
  selector: 'app-auth-button',
  template: `
    <ng-container *ngIf="auth.isAuthenticated$ | async; else loggedOut">
      <button (click)="logOut()">
        Log out
      </button>
    </ng-container>

    <ng-template #loggedOut>
      <button (click)="login()">Log in</button>
    </ng-template>
  `,
  styles: [
    'button { display: block; font-size:2.9rem; margin: auto; color: white; background-color:#2196F3; border:none; border-radius:3px; cursor:pointer;padding:0.5rem 2rem;}'
          ]
})
export class AuthButtonComponent  {

  constructor(@Inject(DOCUMENT) public document: Document,
    public auth: AuthService,
    private user: UserAuthService) {

  }

  ngOnInit(): void {}


  login() {

    this.auth.loginWithRedirect(() => this.user.getUserProfile());

  }


  logOut() {
    this.auth.logout({ returnTo: document.location.origin })
  }



}
